python3 << 'PYEOF'
import json

# Load the updated databases
with open('/tmp/peptides_for_app.json', 'r') as f:
    peptides = json.load(f)

with open('/tmp/stacks_for_app.json', 'r') as f:
    stacks = json.load(f)

# Create the HTML app with embedded data
html_template = '''<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Peptide Protocols & Stacks - Updated</title>
    <script crossorigin src="https://unpkg.com/react@18/umd/react.production.min.js"></script>
    <script crossorigin src="https://unpkg.com/react-dom@18/umd/react-dom.production.min.js"></script>
    <script src="https://unpkg.com/@babel/standalone/babel.min.js"></script>
    <script src="https://cdn.tailwindcss.com"></script>
    <style>
        body {
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
        }
        .gradient-bg {
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        }
        .card {
            transition: transform 0.2s, box-shadow 0.2s;
        }
        .card:hover {
            transform: translateY(-4px);
            box-shadow: 0 12px 24px rgba(0,0,0,0.15);
        }
        .badge {
            display: inline-block;
            padding: 0.25rem 0.75rem;
            border-radius: 9999px;
            font-size: 0.75rem;
            font-weight: 600;
        }
        .ordered-badge {
            background: linear-gradient(135deg, #10b981 0%, #059669 100%);
            color: white;
            box-shadow: 0 2px 4px rgba(16, 185, 129, 0.3);
        }
        .discussed-badge {
            background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
            color: white;
            box-shadow: 0 2px 4px rgba(139, 92, 246, 0.3);
        }
    </style>
</head>
<body class="bg-gray-50">
    <div id="root"></div>

    <script type="text/babel">
        const { useState } = React;

        const PEPTIDES_DATA = ''' + json.dumps(peptides, indent=2) + ''';
        const STACKS_DATA = ''' + json.dumps(stacks, indent=2) + ''';

        function App() {
            const [view, setView] = useState('home');
            const [selectedPeptide, setSelectedPeptide] = useState(null);
            const [selectedStack, setSelectedStack] = useState(null);
            const [searchTerm, setSearchTerm] = useState('');
            const [filterCategory, setFilterCategory] = useState('all');
            const [filterOrderStatus, setFilterOrderStatus] = useState('all');

            const categories = [...new Set(Object.values(PEPTIDES_DATA).map(p => p.category))];

            const filteredPeptides = Object.values(PEPTIDES_DATA).filter(peptide => {
                const matchesSearch = peptide.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                    peptide.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
                                    peptide.type.toLowerCase().includes(searchTerm.toLowerCase());
                const matchesCategory = filterCategory === 'all' || peptide.category === filterCategory;
                const matchesOrderStatus = filterOrderStatus === 'all' ||
                    (filterOrderStatus === 'ordered' && peptide.ordered) ||
                    (filterOrderStatus === 'discussed' && !peptide.ordered);
                return matchesSearch && matchesCategory && matchesOrderStatus;
            });

            const orderedCount = Object.values(PEPTIDES_DATA).filter(p => p.ordered).length;
            const discussedCount = Object.values(PEPTIDES_DATA).filter(p => !p.ordered).length;

            const getCostColor = (tier) => {
                switch(tier) {
                    case 'low': return 'bg-green-100 text-green-800';
                    case 'medium': return 'bg-yellow-100 text-yellow-800';
                    case 'high': return 'bg-red-100 text-red-800';
                    default: return 'bg-gray-100 text-gray-800';
                }
            };

            return (
                <div className="min-h-screen">
                    <header className="gradient-bg text-white shadow-lg">
                        <div className="max-w-7xl mx-auto px-4 py-6">
                            <h1 className="text-4xl font-bold">Peptide Protocols & Stacks</h1>
                            <p className="mt-2 text-purple-100">Your personalized peptide database - {orderedCount} ordered, {discussedCount} discussed</p>
                        </div>
                    </header>

                    <nav className="bg-white shadow-sm sticky top-0 z-10">
                        <div className="max-w-7xl mx-auto px-4">
                            <div className="flex space-x-8 py-4">
                                <button onClick={() => setView('home')} className={`px-4 py-2 rounded-lg font-medium ${view === 'home' ? 'bg-purple-600 text-white' : 'text-gray-600 hover:bg-gray-100'}`}>
                                    Home
                                </button>
                                <button onClick={() => setView('peptides')} className={`px-4 py-2 rounded-lg font-medium ${view === 'peptides' ? 'bg-purple-600 text-white' : 'text-gray-600 hover:bg-gray-100'}`}>
                                    All Peptides ({Object.keys(PEPTIDES_DATA).length})
                                </button>
                                <button onClick={() => setView('stacks')} className={`px-4 py-2 rounded-lg font-medium ${view === 'stacks' ? 'bg-purple-600 text-white' : 'text-gray-600 hover:bg-gray-100'}`}>
                                    Protocol Stacks ({Object.keys(STACKS_DATA).length})
                                </button>
                            </div>
                        </div>
                    </nav>

                    <main className="max-w-7xl mx-auto px-4 py-8">
                        {view === 'home' && (
                            <div>
                                <div className="bg-white rounded-lg shadow-md p-8 mb-8">
                                    <h2 className="text-3xl font-bold text-gray-800 mb-4">Your Peptide Protocol Database</h2>
                                    <p className="text-gray-600 mb-4">
                                        Based on your conversation and orders, this database covers {Object.keys(PEPTIDES_DATA).length} peptides and {Object.keys(STACKS_DATA).length} protocol stacks.
                                    </p>
                                    <div className="grid md:grid-cols-4 gap-6 mt-8">
                                        <div className="bg-green-50 p-6 rounded-lg border-2 border-green-200">
                                            <h3 className="text-2xl font-bold text-green-800 mb-2">{orderedCount} Ordered</h3>
                                            <p className="text-gray-600">Peptides you purchased</p>
                                        </div>
                                        <div className="bg-purple-50 p-6 rounded-lg border-2 border-purple-200">
                                            <h3 className="text-2xl font-bold text-purple-800 mb-2">{discussedCount} Discussed</h3>
                                            <p className="text-gray-600">For future consideration</p>
                                        </div>
                                        <div className="bg-blue-50 p-6 rounded-lg border-2 border-blue-200">
                                            <h3 className="text-2xl font-bold text-blue-800 mb-2">{Object.keys(STACKS_DATA).length} Stacks</h3>
                                            <p className="text-gray-600">Synergistic protocols</p>
                                        </div>
                                        <div className="bg-orange-50 p-6 rounded-lg border-2 border-orange-200">
                                            <h3 className="text-2xl font-bold text-orange-800 mb-2">Evidence-Based</h3>
                                            <p className="text-gray-600">Research-backed info</p>
                                        </div>
                                    </div>
                                </div>

                                <div className="mb-8">
                                    <h3 className="text-2xl font-bold text-gray-800 mb-4">📦 Peptides You Ordered</h3>
                                    <div className="grid md:grid-cols-3 lg:grid-cols-4 gap-4">
                                        {Object.values(PEPTIDES_DATA).filter(p => p.ordered).map(peptide => (
                                            <div key={peptide.id} className="card bg-white rounded-lg shadow-md p-4 cursor-pointer border-2 border-green-200"
                                                 onClick={() => { setSelectedPeptide(peptide); setView('peptide-detail'); }}>
                                                <div className="flex items-center justify-between mb-2">
                                                    <h4 className="text-sm font-bold text-gray-800">{peptide.name}</h4>
                                                    <span className="ordered-badge badge text-xs">✓ ORDERED</span>
                                                </div>
                                                <p className="text-xs text-gray-600 mb-1">{peptide.category}</p>
                                                <span className={`badge text-xs ${getCostColor(peptide.cost_tier)}`}>
                                                    {peptide.cost_tier}
                                                </span>
                                            </div>
                                        ))}
                                    </div>
                                </div>

                                <h3 className="text-2xl font-bold text-gray-800 mb-4">💭 Discussed for Future</h3>
                                <div className="grid md:grid-cols-3 gap-4 mb-8">
                                    {Object.values(PEPTIDES_DATA).filter(p => !p.ordered).map(peptide => (
                                        <div key={peptide.id} className="card bg-white rounded-lg shadow-md p-4 cursor-pointer border-2 border-purple-200"
                                             onClick={() => { setSelectedPeptide(peptide); setView('peptide-detail'); }}>
                                            <div className="flex items-center justify-between mb-2">
                                                <h4 className="text-sm font-bold text-gray-800">{peptide.name}</h4>
                                                <span className="discussed-badge badge text-xs">DISCUSSED</span>
                                            </div>
                                            <p className="text-xs text-gray-600">{peptide.category}</p>
                                        </div>
                                    ))}
                                </div>

                                <h3 className="text-2xl font-bold text-gray-800 mb-4">🎯 Recommended Stacks (Using Your Ordered Peptides)</h3>
                                <div className="grid md:grid-cols-2 gap-6">
                                    {Object.values(STACKS_DATA).filter(s => s.stack_status === "All peptides ordered").slice(0, 4).map(stack => (
                                        <div key={stack.id} className="card bg-white rounded-lg shadow-md p-6 cursor-pointer border-2 border-green-200"
                                             onClick={() => { setSelectedStack(stack); setView('stack-detail'); }}>
                                            <div className="flex items-center justify-between mb-3">
                                                <h4 className="text-xl font-bold text-gray-800">{stack.name}</h4>
                                                <span className="ordered-badge badge">ALL ORDERED</span>
                                            </div>
                                            <p className="text-gray-600 text-sm mb-3">{stack.mechanism.substring(0, 150)}...</p>
                                            <div className="flex flex-wrap gap-2 mb-3">
                                                {stack.peptides.map(pid => (
                                                    <span key={pid} className="badge bg-green-100 text-green-800 text-xs">
                                                        {PEPTIDES_DATA[pid]?.name}
                                                    </span>
                                                ))}
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {view === 'peptides' && (
                            <div>
                                <div className="mb-6 bg-white p-6 rounded-lg shadow-md">
                                    <input
                                        type="text"
                                        placeholder="Search peptides..."
                                        className="w-full px-4 py-2 border border-gray-300 rounded-lg mb-4"
                                        value={searchTerm}
                                        onChange={(e) => setSearchTerm(e.target.value)}
                                    />

                                    <div className="mb-4">
                                        <p className="text-sm font-semibold text-gray-700 mb-2">Filter by Order Status:</p>
                                        <div className="flex flex-wrap gap-2">
                                            <button onClick={() => setFilterOrderStatus('all')}
                                                    className={`px-4 py-2 rounded-lg ${filterOrderStatus === 'all' ? 'bg-purple-600 text-white' : 'bg-gray-200'}`}>
                                                All ({Object.keys(PEPTIDES_DATA).length})
                                            </button>
                                            <button onClick={() => setFilterOrderStatus('ordered')}
                                                    className={`px-4 py-2 rounded-lg ${filterOrderStatus === 'ordered' ? 'bg-green-600 text-white' : 'bg-gray-200'}`}>
                                                📦 Ordered ({orderedCount})
                                            </button>
                                            <button onClick={() => setFilterOrderStatus('discussed')}
                                                    className={`px-4 py-2 rounded-lg ${filterOrderStatus === 'discussed' ? 'bg-purple-600 text-white' : 'bg-gray-200'}`}>
                                                💭 Discussed ({discussedCount})
                                            </button>
                                        </div>
                                    </div>

                                    <div>
                                        <p className="text-sm font-semibold text-gray-700 mb-2">Filter by Category:</p>
                                        <div className="flex flex-wrap gap-2">
                                            <button onClick={() => setFilterCategory('all')}
                                                    className={`px-4 py-2 rounded-lg text-sm ${filterCategory === 'all' ? 'bg-purple-600 text-white' : 'bg-gray-200'}`}>
                                                All
                                            </button>
                                            {categories.map(cat => (
                                                <button key={cat} onClick={() => setFilterCategory(cat)}
                                                        className={`px-4 py-2 rounded-lg text-sm ${filterCategory === cat ? 'bg-purple-600 text-white' : 'bg-gray-200'}`}>
                                                    {cat}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                </div>

                                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                                    {filteredPeptides.map(peptide => (
                                        <div key={peptide.id} className={`card bg-white rounded-lg shadow-md p-6 cursor-pointer border-2 ${peptide.ordered ? 'border-green-200' : 'border-purple-200'}`}
                                             onClick={() => { setSelectedPeptide(peptide); setView('peptide-detail'); }}>
                                            <div className="flex justify-between items-start mb-3">
                                                <h3 className="text-xl font-bold text-gray-800">{peptide.name}</h3>
                                                <div className="flex flex-col gap-1">
                                                    {peptide.ordered ? (
                                                        <span className="ordered-badge badge">ORDERED</span>
                                                    ) : (
                                                        <span className="discussed-badge badge">DISCUSSED</span>
                                                    )}
                                                    <span className={`badge ${getCostColor(peptide.cost_tier)}`}>
                                                        {peptide.cost_tier}
                                                    </span>
                                                </div>
                                            </div>
                                            <p className="text-sm text-purple-600 mb-2">{peptide.type}</p>
                                            <p className="text-sm text-gray-600 mb-3">{peptide.category}</p>
                                            <p className="text-sm text-gray-700 mb-3 line-clamp-3">{peptide.mechanism}</p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {view === 'stacks' && (
                            <div className="grid md:grid-cols-2 gap-6">
                                {Object.values(STACKS_DATA).map(stack => (
                                    <div key={stack.id} className={`card bg-white rounded-lg shadow-md p-6 cursor-pointer border-2 ${
                                        stack.stack_status === 'All peptides ordered' ? 'border-green-200' : 'border-purple-200'
                                    }`}
                                         onClick={() => { setSelectedStack(stack); setView('stack-detail'); }}>
                                        <div className="flex justify-between items-start mb-3">
                                            <h3 className="text-2xl font-bold text-gray-800">{stack.name}</h3>
                                            {stack.stack_status === 'All peptides ordered' && (
                                                <span className="ordered-badge badge">ALL ORDERED</span>
                                            )}
                                        </div>
                                        <p className="text-gray-600 mb-4">{stack.mechanism}</p>
                                        <div className="mb-4">
                                            <p className="text-sm font-semibold text-gray-700 mb-2">Peptides:</p>
                                            <div className="flex flex-wrap gap-2">
                                                {stack.peptides.map(pid => {
                                                    const p = PEPTIDES_DATA[pid];
                                                    return (
                                                        <span key={pid} className={`badge ${p?.ordered ? 'bg-green-100 text-green-800' : 'bg-purple-100 text-purple-800'}`}>
                                                            {p?.name} {p?.ordered ? '✓' : ''}
                                                        </span>
                                                    );
                                                })}
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}

                        {view === 'peptide-detail' && selectedPeptide && (
                            <div>
                                <button onClick={() => setView('peptides')}
                                        className="mb-4 px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300">
                                    ← Back to Peptides
                                </button>
                                <div className={`bg-white rounded-lg shadow-lg p-8 border-l-4 ${selectedPeptide.ordered ? 'border-green-500' : 'border-purple-500'}`}>
                                    <div className="flex justify-between items-start mb-6">
                                        <div>
                                            <h2 className="text-3xl font-bold text-gray-800">{selectedPeptide.name}</h2>
                                            <p className="text-lg text-purple-600 mt-1">{selectedPeptide.type}</p>
                                        </div>
                                        <div className="flex flex-col gap-2">
                                            {selectedPeptide.ordered ? (
                                                <span className="ordered-badge badge text-lg">✓ ORDERED</span>
                                            ) : (
                                                <span className="discussed-badge badge text-lg">DISCUSSED ONLY</span>
                                            )}
                                            <span className={`badge text-lg ${getCostColor(selectedPeptide.cost_tier)}`}>
                                                {selectedPeptide.cost_tier} cost
                                            </span>
                                        </div>
                                    </div>

                                    <div className="grid md:grid-cols-2 gap-6 mb-6">
                                        <div>
                                            <h3 className="font-bold text-gray-800 mb-2">Category</h3>
                                            <p className="text-gray-600">{selectedPeptide.category}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-bold text-gray-800 mb-2">Half-Life</h3>
                                            <p className="text-gray-600">{selectedPeptide.half_life}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-bold text-gray-800 mb-2">Dosing</h3>
                                            <p className="text-gray-600">{selectedPeptide.dosing}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-bold text-gray-800 mb-2">Administration</h3>
                                            <p className="text-gray-600">{selectedPeptide.administration}</p>
                                        </div>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Mechanism of Action</h3>
                                        <p className="text-gray-600">{selectedPeptide.mechanism}</p>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Primary Benefits</h3>
                                        <ul className="space-y-2">
                                            {selectedPeptide.primary_benefits.map((benefit, idx) => (
                                                <li key={idx} className="text-gray-600">• {benefit}</li>
                                            ))}
                                        </ul>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Research Findings</h3>
                                        <p className="text-gray-600">{selectedPeptide.research_findings}</p>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">System Targets</h3>
                                        <div className="flex flex-wrap gap-2">
                                            {selectedPeptide.system_targets.map(target => (
                                                <span key={target} className="badge bg-purple-100 text-purple-800">
                                                    {target}
                                                </span>
                                            ))}
                                        </div>
                                    </div>

                                    <div className="grid md:grid-cols-2 gap-6 mb-6">
                                        <div>
                                            <h3 className="font-bold text-gray-800 mb-2">Accessibility</h3>
                                            <p className="text-gray-600">{selectedPeptide.accessibility}</p>
                                        </div>
                                        <div>
                                            <h3 className="font-bold text-gray-800 mb-2">Status</h3>
                                            <p className="text-gray-600">{selectedPeptide.order_status}</p>
                                        </div>
                                    </div>

                                    {selectedPeptide.special_notes && (
                                        <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4">
                                            <p className="text-yellow-800 font-semibold">⭐ Special Notes</p>
                                            <p className="text-yellow-700 mt-1">{selectedPeptide.special_notes}</p>
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}

                        {view === 'stack-detail' && selectedStack && (
                            <div>
                                <button onClick={() => setView('stacks')}
                                        className="mb-4 px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300">
                                    ← Back to Stacks
                                </button>
                                <div className={`bg-white rounded-lg shadow-lg p-8 border-l-4 ${
                                    selectedStack.stack_status === 'All peptides ordered' ? 'border-green-500' : 'border-purple-500'
                                }`}>
                                    <div className="flex justify-between items-start mb-4">
                                        <h2 className="text-3xl font-bold text-gray-800">{selectedStack.name}</h2>
                                        {selectedStack.stack_status === 'All peptides ordered' && (
                                            <span className="ordered-badge badge text-lg">✓ ALL ORDERED</span>
                                        )}
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Peptides in This Stack</h3>
                                        <div className="flex flex-wrap gap-2">
                                            {selectedStack.peptides.map(pid => {
                                                const p = PEPTIDES_DATA[pid];
                                                return (
                                                    <span key={pid} 
                                                          className={`badge text-lg cursor-pointer ${p?.ordered ? 'ordered-badge' : 'discussed-badge'}`}
                                                          onClick={() => { setSelectedPeptide(p); setView('peptide-detail'); }}>
                                                        {p?.name} {p?.ordered ? '✓' : ''}
                                                    </span>
                                                );
                                            })}
                                        </div>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Primary Goals</h3>
                                        <ul className="space-y-2">
                                            {selectedStack.primary_goals.map((goal, idx) => (
                                                <li key={idx} className="text-gray-600">• {goal}</li>
                                            ))}
                                        </ul>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Mechanism & Synergy</h3>
                                        <p className="text-gray-600 mb-3">{selectedStack.mechanism}</p>
                                        <p className="text-gray-600 italic">{selectedStack.synergy_notes}</p>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-3">Protocol Details</h3>
                                        <div className="space-y-4">
                                            {Object.entries(selectedStack.protocol).map(([peptideId, details]) => (
                                                <div key={peptideId} className="bg-gray-50 p-4 rounded-lg">
                                                    <h4 className="font-semibold text-purple-700 mb-2">
                                                        {PEPTIDES_DATA[peptideId]?.name || peptideId}
                                                    </h4>
                                                    <div className="grid md:grid-cols-2 gap-3 text-sm">
                                                        {Object.entries(details).map(([key, value]) => (
                                                            <div key={key}>
                                                                <span className="font-medium text-gray-700 capitalize">
                                                                    {key.replace(/_/g, ' ')}:
                                                                </span>
                                                                <span className="text-gray-600 ml-2">{value}</span>
                                                            </div>
                                                        ))}
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Cycling Protocol</h3>
                                        <p className="text-gray-600">{selectedStack.cycling}</p>
                                    </div>

                                    <div className="mb-6">
                                        <h3 className="font-bold text-gray-800 mb-2">Considerations</h3>
                                        <ul className="space-y-2">
                                            {selectedStack.considerations.map((consideration, idx) => (
                                                <li key={idx} className="text-gray-600">• {consideration}</li>
                                            ))}
                                        </ul>
                                    </div>

                                    {selectedStack.special_notes && (
                                        <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-6">
                                            <p className="text-yellow-800 font-semibold">⭐ Special Notes</p>
                                            <p className="text-yellow-700 mt-1">{selectedStack.special_notes}</p>
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}
                    </main>

                    <footer className="bg-gray-800 text-white mt-12 py-8">
                        <div className="max-w-7xl mx-auto px-4 text-center">
                            <p className="text-gray-400">
                                This information is for educational and research purposes only.
                                Always consult with a qualified healthcare provider before starting any peptide protocol.
                            </p>
                            <p className="text-gray-500 mt-2 text-sm">
                                Based on your orders and conversation • {orderedCount} ordered, {discussedCount} discussed • {new Date().getFullYear()}
                            </p>
                        </div>
                    </footer>
                </div>
            );
        }

        const root = ReactDOM.createRoot(document.getElementById('root'));
        root.render(<App />);
    </script>
</body>
</html>'''

# Write the file
with open('/sessions/wonderful-pensive-wright/mnt/outputs/peptide-protocols-app.html', 'w') as f:
    f.write(html_template)

print("✓ Updated web app created with:")
print(f"  • {len(peptides)} peptides (including KPV and Mod GRF 1-29)")
print(f"  • {len(stacks)} protocol stacks")
print("  • Visual distinction between ordered and discussed")
print("  • Filters for order status")
print("  • Updated with all your actual orders")

